            divDiff[i] = y_axis.get(i);
        }
        for (int i = 1; i < n; i++) {
            for (int j = n - 1; j >= i; j--) {
                divDiff[j] = (divDiff[j] - divDiff[j - 1]) / (x_axis.get(j) - x_axis.get(j - i));
            }
            result += divDiff[i] * calculateCoefficients(x0, x_axis, i);
        }
        return result;
    }
    static double calculateCoefficients(double x, List<Double> arr, int n) {
        double result = 1;
        for (int i = 0; i < n; i++) {
            result *= (x - arr.get(i));
        }
        return result;
    }
}


