using System;

namespace Polygons
{
    class Program
    {
        static void Main(string[] args)
        {
            Console.Write("Введите количество вершин: ");
            int n = int.Parse(Console.ReadLine());

            double[,] vertices = new double[n, 2];

            for (int i = 0; i < n; i++)
            {
                Console.Write($"Введите координаты {i+1}-ой вершины (x,y): ");
                string[] input = Console.ReadLine().Split(',');
                vertices[i, 0] = double.Parse(input[0]);
                vertices[i, 1] = double.Parse(input[1]);
            }

            double perimeter = GetPolygonPerimeter(ref vertices);

            Console.WriteLine($"Периметр многоугольника: {perimeter}");
        }

        static double GetDistance(ref double x1, ref double y1, ref double x2, ref double y2)
        {
            return Math.Sqrt(Math.Pow(x2 - x1, 2) + Math.Pow(y2 - y1, 2));
        }

        static double GetPolygonPerimeter(ref double[,] vertices)
        {
            double perimeter = 0;

            for (int i = 0; i < vertices.GetLength(0) - 1; i++)
            {
                perimeter += GetDistance(ref vertices[i, 0], ref vertices[i, 1], ref vertices[i + 1, 0], ref vertices[i + 1, 1]);
            }

            perimeter += GetDistance(ref vertices[0, 0], ref vertices[0, 1], ref vertices[vertices.GetLength(0) - 1, 0], ref vertices[vertices.GetLength(0) - 1, 1]);

            return perimeter;
        }
    }
}

