        {
            Console.Write("Введите количество вершин многоугольника: ");
            int n = int.Parse(Console.ReadLine());

            double[] x = new double[n];
            double[] y = new double[n];

            for (int i = 0; i < n; i++)
            {
                Console.Write($"Введите координаты вершины {i+1}: ");
                string[] input = Console.ReadLine().Split(' ');
                x[i] = double.Parse(input[0]);
                y[i] = double.Parse(input[1]);
            }

            double perimeter = CalculatePerimeter(ref x, ref y);

            Console.WriteLine($"Периметр многоугольника равен {perimeter}");
        }

        static double CalculatePerimeter(ref double[] x, ref double[] y)
        {
            double perimeter = 0;

            for (int i = 0; i < x.Length - 1; i++)
            {
                perimeter += Length(x[i], y[i], x[i + 1], y[i + 1]);
            }

            perimeter += Length(x[x.Length - 1], y[x.Length - 1], x[0], y[0]);

            return perimeter;
        }

        static double Length(double x1, double y1, double x2, double y2)
        {
            return Math.Sqrt((x2 - x1) * (x2 - x1) + (y2 - y1) * (y2 - y1));
        }
    }
}

