{
Next[i][x]=-1;//标记节点不可到达
}
else{
int limit = 3;
while(prev[x]!=i && limit > 0)
{
x=prev[x];//递归找到应该出发的下一个

节点
limit--;
}
if (limit==0){
Next[i][x]=-1; //如果没有找到限制次数内的出发节点，则标记为不可达
}
else{
Next[i][x]=i;//将找到的可达节点加入Next矩阵
}
}
}
}
