```
// 定义游戏相关的变量
let ROW = 10; // 行数
let COL = 10; // 列数
let BOMB_COUNT = 10; // 雷数
let board = []; // 二维数组表示棋盘

// 初始化棋盘
function initBoard() {
  // 初始化棋盘
  // 将二维数组board的每个元素都初始化
  for (let i = 0; i < ROW; i++) {
    board[i] = [];
    for (let j = 0; j < COL; j++) {
      board[i][j] = {}; // 每个格子是一个对象，包含以下属性：isBomb(是否是雷)、isShow(是否已经显示过)、isFlag(是否标记旗子)、bombCount(周围雷的数量)
      board[i][j].isBomb = false;
      board[i][j].isShow = false;
      board[i][j].isFlag = false;
      board[i][j].bombCount = 0;
    }
  }

  // 随机生成雷的位置
  let bombLeft = BOMB_COUNT;
  while (bombLeft > 0) {
    let randRow = Math.floor(Math.random() * ROW);
    let randCol = Math.floor(Math.random() * COL);
    if (!board[randRow][randCol].isBomb) {
      board[randRow][randCol].isBomb = true;
      bombLeft--;
    }
  }

  // 计算每个非雷格子周围雷的数量
  for (let i = 0; i < ROW; i++) {
    for (let j = 0; j < COL; j++) {
      if (!board[i][j].isBomb) {
        board[i][j].bombCount = countBomb(i, j);
      }
    }
  }
}

// 计算指定格子周围雷的数量
function countBomb(row, col) {
  let count = 0;

  for (let i = row - 1; i <= row + 1; i++) {
    for (let j = col - 1; j <= col + 1; j++) {
      if (i >= 0 && i < ROW && j >= 0 && j < COL && board[i][j].isBomb) {
        count++;
      }
    }
  }

  return count;
}

// 显示指定格子
function showCell(row, col) {
  let cell = board[row][col];
  if (cell.isShow || cell.isFlag) {
    return; // 如果格子已经显示过或标记旗子，则不操作
  }

  if (cell.isBomb) {
    gameOver(); // 如果单击了雷，则游戏结束
  } else {
    // 显示格子内容
    cell.isShow = true;
    updateBoard(row, col, cell.bombCount);

    // 如果格子周围没有雷，递归显示周围8个格子
    if (cell.bombCount === 0) {
      for (let i = row - 1; i <= row + 1; i++) {
        for (let j = col - 1; j <= col + 1; j++) {
          if (i >= 0 && i < ROW && j >= 0 && j < COL) {
            showCell(i, j);
          }
        }
      }
    }
  }

  // 判断游戏是否胜利
  if (checkWin()) {
    gameWin();
  }
}

// 标记旗子
function flagCell(row, col) {
  let cell = board[row][col];
  if (cell.isShow) {
    return; // 如果格子已经显示过，则不操作
  }

  cell.isFlag = !cell.isFlag;
  updateBoard(row, col, cell.isFlag ? -1 : cell.bombCount);

  // 判断游戏是否胜利
  if (checkWin()) {
    gameWin();
  }
}

// 判断游戏是否胜利
function checkWin() {
  for (let i = 0; i < ROW; i++) {
    for (let j = 0; j < COL; j++) {
      let cell = board[i][j];
      if (!cell.isBomb && !cell.isShow) {
        return false;
      }
    }
  }

  return true;
}

// 游戏结束
function gameOver() {
  // 显示所有雷的位置
  for (let i = 0; i < ROW; i++) {
    for (let j = 0; j < COL; j++) {
      if (board[i][j].isBomb) {
        updateBoard(i, j, "雷");
      }
    }
  }

  alert("游戏结束！");
}

// 游戏胜利
