	CameraComponent->PostProcessSettings.AddBlendable(TSubclassOf<UMaterialInterface>(UMaterial::StaticClass()));
	CameraComponent->PostProcessSettings.bOverride_FilmGrainIntensity = true;
	CameraComponent->PostProcessSettings.FilmGrainIntensity = 0.f;
	CameraComponent->PostProcessSettings.bOverride_VignetteIntensity = true;
	CameraComponent->PostProcessSettings.VignetteIntensity = 0.f;
	CameraComponent->PostProcessSettings.bOverride_FilmGrainJitter = true;
	CameraComponent->PostProcessSettings.FilmGrainJitter = FVector2D(1.f, 1.f);
	CameraComponent->PostProcessSettings.bOverride_LensFlareIntensity = true;
	CameraComponent->PostProcessSettings.LensFlareIntensity = 0.f;
	CameraComponent->PostProcessSettings.bOverride_SceneFringeIntensity = true;
	CameraComponent->PostProcessSettings.SceneFringeIntensity = 0.f;

	// Устанавливаем рабочее расстояние
	CameraComponent->SetRelativeLocation(FVector(0.f, 0.f, 0.f));
	CameraComponent->bUsePawnControlRotation = true;

	// Настройки управления
	InputComponent->BindAxis("Forward", this, &AFirstPersonCamera::AddMovementInput);
	InputComponent->BindAxis("Strafe", this, &AFirstPersonCamera::AddMovementInput);
	InputComponent->BindAxis("Turn", this, &AFirstPersonCamera::AddControllerYawInput);
	InputComponent->BindAxis("LookUp", this, &AFirstPersonCamera::AddControllerPitchInput);
	InputComponent->BindAction("Sprint", IE_Pressed, this, &AFirstPersonCamera::SetSprintSpeed, 2.f);
	InputComponent->BindAction("Sprint", IE_Released, this, &AFirstPersonCamera::SetSprintSpeed, 1.f);

	// Установка параметров
	BobbingTimer = 0.f;
	BobbingRatio = 0.f;
	MovementSpeed = 300.f;
	SprintSpeedMultiplier = 2.f;
	HeadBobFrequency = 2.f;
	HeadBobInterval = 2.f;
	HeadBobThreshold = 1.5f;
	MovementComponent->MaxSpeed = MovementSpeed;
}

void AFirstPersonCamera::BeginPlay()
{
	Super::BeginPlay();
	LastLocation = GetActorLocation();
}

void AFirstPersonCamera::Tick(float DeltaTime)
{
	Super::Tick(DeltaTime);

	// Рассчитывем новое ускорение
	UpdateInput(DeltaTime);

	// Рассчитываем смещение камеры с помощью качания головы в зависимости от ускорения
	FVector CurrentLocation = GetActorLocation();
	FVector ViewDirection = FRotationMatrix(GetControlRotation()).GetScaledAxis(EAxis::X);
	float DistanceTraveled = FVector::DotProduct(CurrentLocation - LastLocation, ViewDirection);
	if (DistanceTraveled > HeadBobThreshold)
	{
		float BobbingFrequency = HeadBobFrequency * (1.f + Acceleration * SprintSpeedMultiplier * 0.1f);
		float BobbingAmount = FMath::Abs(DistanceTraveled) * 0.5f * FMath::Sin(BobbingTimer * BobbingFrequency) * FMath::Pow(MovementSpeed, 0.2f);

		FVector BobbingDirection = ProjectVectorOntoPlane(ViewDirection, FVector::UpVector);
		AddActorWorldOffset(BobbingDirection * BobbingAmount);
		BobbingTimer += DeltaTime;
		if (BobbingTimer > HeadBobInterval)
		{
			BobbingTimer = 0.f;
		}
	}

	LastLocation = CurrentLocation;
}

void AFirstPersonCamera::SetupPlayerInputComponent(UInputComponent* PlayerInputComponent)
{
	Super::SetupPlayerInputComponent(PlayerInputComponent);
}

void AFirstPersonCamera::SetMovementSpeed(float NewMovementSpeed)
{
	MovementSpeed = NewMovementSpeed;
	MovementComponent->MaxSpeed = MovementSpeed;
}

void AFirstPersonCamera::UpdateInput(float DeltaTime)
{
	// Прибавляем скорость вверх к текущему вектору
	float ForwardValue = GetInputAxisValue("Forward");
	float StrafeValue = GetInputAxisValue("Strafe");
	FVector ForwardDirection = FVector(ForwardValue, StrafeValue, 0.f).GetClampedToMaxSize(1.f);

	float TimeSinceLastUpdate = UGameplayStatics::GetTimeSeconds(this) - LastUpdate;
	float MaxLinearSpeed = MovementComponent->GetMaxSpeed();

	float CurrentAcceleration = Acceleration;
	float TargetAcceleration = ForwardDirection.Size() * MaxLinearSpeed * 4.f;

	Acceleration = FMath::FInterpTo(CurrentAcceleration, TargetAcceleration, DeltaTime, MaxLinearSpeed);

	LastUpdate = UGameplayStatics::GetTimeSeconds(this);
}

void AFirstPersonCamera::SetSprintSpeed(float SprintSpeedMultiplier)
{
	this->SprintSpeedMultiplier = SprintSpeedMultiplier;
}

FVector AFirstPersonCamera::ProjectVectorOntoPlane(FVector InVector, FVector InNormal)
{
	FVector Result = InVector - FVector::DotProduct(InVector, InNormal) * InNormal;
	return Result;
}
