```cpp
void gravarAlunosNoArquivo(Aluno* alunos[], int numAlunos, const std::string &arquivo) {
    std::ofstream ofs(arquivo, std::ios::binary);

    if (!ofs) {
        std::cerr << "Não foi possível abrir o arquivo " << arquivo << " para escrita.\n";
        return;
    }

    ofs.write(reinterpret_cast<char*>(&numAlunos), sizeof(int));

    for (int i = 0; i < numAlunos; i++) {
        int numProcesso = alunos[i]->getNumProcesso();
        std::string nome = alunos[i]->getNome();
        std::string morada = alunos[i]->getMorada();
        std::string dataNasc = alunos[i]->getDataNasc();
        int anoEscolaridade = alunos[i]->getAnoEscolaridade();

        ofs.write(reinterpret_cast<char*>(&numProcesso), sizeof(int));
        size_t nomeSize = nome.size();
        ofs.write(reinterpret_cast<char*>(&nomeSize), sizeof(size_t));
        ofs.write(nome.c_str(), nomeSize);
        size_t moradaSize = morada.size();
        ofs.write(reinterpret_cast<char*>(&moradaSize), sizeof(size_t));
        ofs.write(morada.c_str(), moradaSize);
        size_t dataNascSize = dataNasc.size();
        ofs.write(reinterpret_cast<char*>(&dataNascSize), sizeof(size_t));
        ofs.write(dataNasc.c_str(), dataNascSize);
        ofs.write(reinterpret_cast<char*>(&anoEscolaridade), sizeof(int));
    }

    ofs.close();
    std::cout << "Alunos gravados no arquivo com sucesso!\n";
}

void lerAlunosDoArquivo(Aluno* alunos[], int &numAlunos, const std::string &arquivo) {
    std::ifstream ifs(arquivo, std::ios::binary);

    if (!ifs) {
        std::cerr << "Não foi possível abrir o arquivo " << arquivo << " para leitura.\n";
        return;
    }

    ifs.read(reinterpret_cast<char*>(&numAlunos), sizeof(int));

    for (int i = 0; i < numAlunos; i++) {
        int numProcesso, anoEscolaridade;
        std::string nome, morada, dataNasc;
        size_t nomeSize, moradaSize, dataNascSize;

        ifs.read(reinterpret_cast<char*>(&numProcesso), sizeof(int));
        ifs.read(reinterpret_cast<char*>(&nomeSize), sizeof(size_t));
        nome.resize(nomeSize);
        ifs.read(&nome[0], nomeSize);
        ifs.read(reinterpret_cast<char*>(&moradaSize), sizeof(size_t));
        morada.resize(moradaSize);
        ifs.read(&morada[0], moradaSize);
        ifs.read(reinterpret_cast<char*>(&dataNascSize), sizeof(size_t));
        dataNasc.resize(dataNascSize);
        ifs.read(&dataNasc[0], dataNascSize);
        ifs.read(reinterpret_cast<char*>(&anoEscolaridade), sizeof(int));

        alunos[i] = new Aluno(numProcesso, nome, morada, dataNasc, anoEscolaridade);
    }

    ifs.close();
    std::cout << "Alunos lidos do arquivo com sucesso!\n";
}

void eliminarAlunoPeloNome(Aluno* alunos[], int &numAlunos) {
    std::string nome;
    std::cout << "Insira o nome do aluno a ser eliminado: ";
    std::cin.ignore(); // Ignora a quebra de linha após a entrada anterior
    std::getline(std::cin, nome);

    int indiceAluno = pesquisarAluno(alunos, numAlunos, nome);

    if (indiceAluno != -1) {
        delete alunos[indiceAluno];
        for (int i = indiceAluno; i < numAlunos - 1; i++) {
            alunos[i] = alunos[i + 1];
        }
        numAlunos--;
        std::cout << "Aluno eliminado com sucesso!" << std::endl;
    } else {
        std::cout << "Aluno não encontrado." << std::endl;
    }
}
