Here's an example on how to create an SQLite database with User and Card tables and perform basic CRUD operations:

```csharp
using System;
using System.Data;
using System.Data.SQLite;

class SQLiteDatabase
{
    private SQLiteConnection _connection;

    public SQLiteDatabase()
    {
        _connection = new SQLiteConnection("Data Source=MyDatabase.sqlite;Version=3;");
        _connection.Open();
        InitializeTables();
    }

    private void InitializeTables()
    {
        string createUsersTable = @"CREATE TABLE IF NOT EXISTS Users(
            Id TEXT PRIMARY KEY
        );";

        string createCardsTable = @"CREATE TABLE IF NOT EXISTS Cards(
            Id TEXT PRIMARY KEY,
            UserId TEXT,
            Units REAL,
            Remaining INTEGER,
            Timestamp INTEGER,
            FOREIGN KEY(UserId) REFERENCES Users(Id)
        );";

        using (SQLiteCommand command = new SQLiteCommand(createUsersTable, _connection))
        {
            command.ExecuteNonQuery();
        }

        using (SQLiteCommand command = new SQLiteCommand(createCardsTable, _connection))
        {
            command.ExecuteNonQuery();
        }
    }

    public void AddUser(string id)
    {
        string addUser = "INSERT OR IGNORE INTO Users (Id) VALUES (@id);";
        using (SQLiteCommand command = new SQLiteCommand(addUser, _connection))
        {
            command.Parameters.AddWithValue("@id", id);
            command.ExecuteNonQuery();
        }
    }

    public void DeleteUser(string id)
    {
        string deleteUser = "DELETE FROM Users WHERE Id=@id;";
        using (SQLiteCommand command = new SQLiteCommand(deleteUser, _connection))
        {
            command.Parameters.AddWithValue("@id", id);
            command.ExecuteNonQuery();
        }
    }

    public void UpdateCard(string id, double units, int remaining)
    {
        string updateCard = @"UPDATE Cards
            SET Units = @units,
                Remaining = @remaining
            WHERE Id = @id;";

        using (SQLiteCommand command = new SQLiteCommand(updateCard, _connection))
        {
            command.Parameters.AddWithValue("@id", id);
            command.Parameters.AddWithValue("@units", units);
            command.Parameters.AddWithValue("@remaining", remaining);
            command.ExecuteNonQuery();
        }
    }

