        if (foundCard != null)
        {
            Console.WriteLine($"Card Id: {foundCard.Id}, Units: {foundCard.Units}, Remaining: {foundCard.Remaining}");
        }

        // Assign the card "123456789012345678" to the user "12345678901" at timestamp 1609459200
        db.ExecuteNonQuery(@"INSERT INTO UserCards (UserId, CardId, Timestamp)
            VALUES (@userId, @cardId, @timestamp);",
            new SQLiteParameter("@userId", user.Id),
            new SQLiteParameter("@cardId", card.Id),
            new SQLiteParameter("@timestamp", 1609459200));

        // Get user's cards and print their ids
        var userCards = db.GetUserCards(user.Id);
        Console.WriteLine("User's cards:");
        foreach (var userCard in userCards)
        {
            Console.WriteLine(userCard.Id);
        }

        db.DeleteCard(card.Id);
        db.DeleteUser(user.Id);
    }
}
