    private void ExecuteNonQuery(string commandText)
    {
        using (SQLiteCommand command = new SQLiteCommand(commandText, _connection))
        {
            command.ExecuteNonQuery();
        }
    }

    public void AddUser(User user)
    {
        ExecuteNonQuery("INSERT OR IGNORE INTO Users (Id) VALUES (@id);",
            new SQLiteParameter("@id", user.Id));
    }

    public void DeleteUser(string id)
    {
        ExecuteNonQuery("DELETE FROM Users WHERE Id=@id;",
            new SQLiteParameter("@id", id));
    }

    public User GetUser(string id)
    {
        using (SQLiteCommand command = new SQLiteCommand("SELECT * FROM Users WHERE Id=@id;", _connection))
        {
            command.Parameters.Add(new SQLiteParameter("@id", id));
            using (var result = command.ExecuteReader())
            {
                if(result.Read())
                    return new User { Id = Convert.ToString(result["Id"]) };
            }
        }
        return null;
    }

    public void AddCard(Card card)
    {
        ExecuteNonQuery(@"INSERT OR IGNORE INTO Cards (Id, Units, Remaining)
            VALUES (@id, @units, @remaining);",
            new SQLiteParameter("@id", card.Id),
            new SQLiteParameter("@units", card.Units),
            new SQLiteParameter("@remaining", card.Remaining));
    }

    public void DeleteCard(string id)
    {
        ExecuteNonQuery("DELETE FROM Cards WHERE Id=@id;",
            new SQLiteParameter("@id", id));
    }

    public Card GetCard(string id)
    {
        using (SQLiteCommand command = new SQLiteCommand("SELECT * FROM Cards WHERE Id=@id;", _connection))
        {
            command.Parameters.Add(new SQLiteParameter("@id", id));
            using (var result = command.ExecuteReader())
            {
                if(result.Read())
                    return new Card
                    {
                        Id = Convert.ToString(result["Id"]),
                        Units = Convert.ToDouble(result["Units"]),
                        Remaining = Convert.ToInt32(result["Remaining"])
                    };
            }
        }
        return null;
    }

    public List<Card> GetUserCards(string userId)
    {
        List<Card> userCards = new List<Card>();
        using (SQLiteCommand command = new SQLiteCommand(@"SELECT c.* FROM UserCards uc
                                                            INNER JOIN Cards c ON uc.CardId = c.Id
                                                        WHERE uc.UserId=@userId;", _connection))
        {
            command.Parameters.Add(new SQLiteParameter("@userId", userId));
            using (var result = command.ExecuteReader())
            {
                while (result.Read())
                {
                    userCards.Add(new Card
                    {
                        Id = Convert.ToString(result["Id"]),
                        Units = Convert.ToDouble(result["Units"]),
                        Remaining = Convert.ToInt32(result["Remaining"])
                    });
                }
            }
        }
        return userCards;
    }

    private void ExecuteNonQuery(string commandText, params SQLiteParameter[] parameters)
    {
        using (SQLiteCommand command = new SQLiteCommand(commandText, _connection))
        {
            command.Parameters.AddRange(parameters);
            command.ExecuteNonQuery();
        }
    }

