Here is an example of how to create an SQLite database in C# that tracks users and their associated cards:

using System;
using System.Data.SQLite;

namespace SQLiteExample
{
    class Program
    {
        static void Main(string[] args)
        {
            // Connect to the database
            using (SQLiteConnection connection = new SQLiteConnection("Data Source=database.db;Version=3;"))
            {
                connection.Open();

                // Create the users table
                using (SQLiteCommand command = new SQLiteCommand("CREATE TABLE IF NOT EXISTS users (id TEXT PRIMARY KEY)", connection))
                {
                    command.ExecuteNonQuery();
                }

                // Create the cards table
                using (SQLiteCommand command = new SQLiteCommand("CREATE TABLE IF NOT EXISTS cards (id TEXT PRIMARY KEY, units REAL, remaining INTEGER, timestamp INTEGER)", connection))
                {
                    command.ExecuteNonQuery();
                }

                // Create the user_cards table
                using (SQLiteCommand command = new SQLiteCommand("CREATE TABLE IF NOT EXISTS user_cards (user_id TEXT, card_id TEXT, timestamp INTEGER, PRIMARY KEY (user_id, card_id))", connection))
                {
                    command.ExecuteNonQuery();
                }

                // Test the database
                User user = GetOrAddUser("12345678901", connection);
                Card card = GetOrAddCard("abcdefghijk", 5.0, 10, DateTime.Now.Ticks, connection);
                AddUserCard(user, card, DateTime.Now.Ticks, connection);
                Console.WriteLine($"User {user.Id} has card {card.Id} with {card.Units} units remaining.");

                card.Remaining = 8;
                UpdateCard(card, connection);
                var updatedCard = GetCard(card.Id, connection);
                Console.WriteLine("Card updated with remaining units: " + updatedCard.Remaining);

                DeleteCard(card.Id, connection);
                Console.WriteLine($"Card {card.Id} deleted.");

                // Disconnect from the database
                connection.Close();
            }
        }

        static User GetOrAddUser(string id, SQLiteConnection connection)
        {
            // Check if the user exists
            using (SQLiteCommand command = new SQLiteCommand("SELECT id FROM users WHERE id = @id", connection))
            {
                command.Parameters.AddWithValue("@id", id);

                using (SQLiteDataReader reader = command.ExecuteReader())
                {
                    if (reader.Read())
                    {
                        // User already exists, return it
                        return new User(id);
                    }
                    else
                    {
                        // User does not exist, add it
                        using (SQLiteCommand insertCommand = new SQLiteCommand("INSERT INTO users (id) VALUES (@id)", connection))
                        {
                            insertCommand.Parameters.AddWithValue("@id", id);
                            insertCommand.ExecuteNonQuery();

                            return new User(id);
                        }
                    }
                }
            }
        }

        static Card GetOrAddCard(string id, double units, int remaining, long timestamp, SQLiteConnection connection)
        {
            // Check if the card exists
            using (SQLiteCommand command = new SQLiteCommand("SELECT id FROM cards WHERE id = @id", connection))
            {
                command.Parameters.AddWithValue("@id", id);

                using (SQLiteDataReader reader = command.ExecuteReader())
                {
                    if (reader.Read())
                    {
                        // Card already exists, return it
                        return GetCard(id, connection);
                    }
                    else
                    {
                        // Card does not exist, add it
                        using (SQLiteCommand insertCommand = new SQLiteCommand("INSERT INTO cards (id, units, remaining, timestamp) VALUES (@id, @units, @remaining, @timestamp)", connection))
                        {
                            insertCommand.Parameters.AddWithValue("@id", id);
                            insertCommand.Parameters.AddWithValue("@units", units);
                            insertCommand.Parameters.AddWithValue("@remaining", remaining);
                            insertCommand.Parameters.AddWithValue("@timestamp", timestamp);
                            insertCommand.ExecuteNonQuery();

                            return new Card(id, units, remaining, timestamp);
                        }
                    }
                }
            }
        }

        static void AddUserCard(User user, Card card, long timestamp, SQLiteConnection connection)
        {
            // Add the user-card association
            using (SQLiteCommand command = new SQLiteCommand("INSERT INTO user_cards (user_id, card_id, timestamp) VALUES (@user_id, @card_id, @timestamp)", connection))
            {
                command.Parameters.AddWithValue("@user_id", user.Id);
                command.Parameters.AddWithValue("@card_id", card.Id);
                command.Parameters.AddWithValue("@timestamp", timestamp);
                command.ExecuteNonQuery();
            }
        }

        static Card GetCard(string id, SQLiteConnection connection)
        {
            // Get the card details
            using (SQLiteCommand command = new SQLiteCommand("SELECT units, remaining, timestamp FROM cards WHERE id = @id", connection))
            {
                command.Parameters.AddWithValue("@id", id);

                using (SQLiteDataReader reader = command.ExecuteReader())
                {
                    reader.Read();
                    double units = reader.GetDouble(0);
                    int remaining = reader.GetInt32(1);
                    long timestamp = reader.GetInt64(2);

                    return new Card(id, units, remaining, timestamp);
                }
            }
        }

        static void UpdateCard(Card card, SQLiteConnection connection)
        {
            // Update the card details
            using (SQLiteCommand command = new SQLiteCommand("UPDATE cards SET units = @units, remaining = @remaining, timestamp = @timestamp WHERE id = @id", connection))
            {
                command.Parameters.AddWithValue("@units", card.Units);
                command.Parameters.AddWithValue("@remaining", card.Remaining);
                command.Parameters.AddWithValue("@timestamp", card.Timestamp);
                command.Parameters.AddWithValue("@id", card.Id);
                command.ExecuteNonQuery();
            }
        }

        static void DeleteCard(string id, SQLiteConnection connection)
        {
            // Delete the card
            using (SQLiteCommand command = new SQLiteCommand("DELETE FROM cards WHERE id = @id", connection))
            {
                command.Parameters.AddWithValue("@id", id);
                command.ExecuteNonQuery();
            }
        }
    }

    class User
    {
        public string Id { get; }

        public User(string id)
        {
            Id = id;
        }
    }

    class Card
    {
        public string Id { get; }
        public double Units { get; set; }
        public int Remaining { get; set; }
        public long Timestamp { get; set; }

        public Card(string id, double units, int remaining, long timestamp)
        {
            Id = id;
            Units = units;
            Remaining = remaining;
            Timestamp = timestamp;
        }
    }
}

using System;
using System.Data.SQLite;

public class DatabaseManager
{
    private readonly string _connectionString;
    private SQLiteConnection _connection;

    public DatabaseManager(string connectionString)
    {
        _connectionString = connectionString;
    }

    public void OpenConnection()
    {
        _connection = new SQLiteConnection(_connectionString);
        _connection.Open();
    }

    public void CloseConnection()
    {
        _connection.Close();
    }

    public void CreateTables()
    {
        string createUserTableQuery = @"CREATE TABLE IF NOT EXISTS Users (
                                        Id VARCHAR(11) PRIMARY KEY,
                                        Name VARCHAR(50) NOT NULL
                                    );";

        string createCardTableQuery = @"CREATE TABLE IF NOT EXISTS Cards (
                                        Id VARCHAR(18) PRIMARY KEY,
                                        Units REAL NOT NULL,
                                        Remaining INTEGER NOT NULL,
                                        Timestamp INTEGER NOT NULL,
                                        UserId VARCHAR(11),
                                        FOREIGN KEY(UserId) REFERENCES Users(Id)
                                    );";

        ExecuteNonQuery(createUserTableQuery);
        ExecuteNonQuery(createCardTableQuery);
    }

    public void InsertUser(string id, string name)
    {
        string query = $"INSERT OR IGNORE INTO Users (Id, Name) VALUES ('{id}', '{name}')";

        ExecuteNonQuery(query);
    }

    public void InsertCard(string id, double units, int remaining, long timestamp, string userId = null)
    {
        string query = $"INSERT INTO Cards (Id, Units, Remaining, Timestamp, UserId) VALUES ('{id}', {units}, {remaining}, {timestamp}, {userId != null ? $"'{userId}'" : "NULL"})";

        ExecuteNonQuery(query);
    }

    public void DeleteUser(string id)
    {
        string query = $"DELETE FROM Users WHERE Id = '{id}'";

        ExecuteNonQuery(query);
    }

    public void DeleteCard(string id)
    {
        string query = $"DELETE FROM Cards WHERE Id = '{id}'";

        ExecuteNonQuery(query);
    }

    public void UpdateUser(string id, string name)
    {
        string query = $"UPDATE Users SET Name = '{name}' WHERE Id = '{id}'";

        ExecuteNonQuery(query);
    }

    public void UpdateCard(string id, double units, int remaining)
    {
        string query = $"UPDATE Cards SET Units = {units}, Remaining = {remaining} WHERE Id = '{id}'";

        ExecuteNonQuery(query);
    }

    public (string, string) GetOrAddUser(string id, string name)
    {
        string query = $"SELECT * FROM Users WHERE Id = '{id}'";

        SQLiteDataReader reader = ExecuteReader(query);

        if (reader.Read())
        {
            string foundId = reader.GetString(0);
            string foundName = reader.GetString(1);

            reader.Close();

            return (foundId, foundName);
        }
        else
        {
            reader.Close();

            InsertUser(id, name);
            return (id, name);
        }
    }

    public (string, double, int, long, string) GetOrAddCard(string id, double units, int remaining, long timestamp, string userId = null)
    {
        string query = $"SELECT * FROM Cards WHERE Id = '{id}'";

        SQLiteDataReader reader = ExecuteReader(query);

        if (reader.Read())
        {
            string foundId = reader.GetString(0);
            double foundUnits = reader.GetDouble(1);
            int foundRemaining = reader.GetInt32(2);
            long foundTimestamp = reader.GetInt64(3);
            string foundUserId = null;

            if (!reader.IsDBNull(4))
            {
                foundUserId = reader.GetString(4);
            }

            reader.Close();

            return (foundId, foundUnits, foundRemaining, foundTimestamp, foundUserId);
        }
        else
        {
            reader.Close();

            InsertCard(id, units, remaining, timestamp, userId);
            return (id, units, remaining, timestamp, userId);
        }
    }

    private void ExecuteNonQuery(string query)
    {
        SQLiteCommand command = new SQLiteCommand(query, _connection);
        command.ExecuteNonQuery();
    }

    private SQLiteDataReader ExecuteReader(string query)
    {
        SQLiteCommand command = new SQLiteCommand(query, _connection);
        SQLiteDataReader reader = command.ExecuteReader();

        return reader;
    }
}

