        static Card GetOrAddCard(string id, double units, int remaining, DateTime timestamp, SQLiteConnection connection)
        {
            // Check if the card exists
            string checkCardQuery = "SELECT * FROM Cards WHERE Id = @Id;";
            SQLiteCommand checkCardCommand = new SQLiteCommand(checkCardQuery, connection);
            checkCardCommand.Parameters.AddWithValue("@Id", id);
            SQLiteDataReader reader = checkCardCommand.ExecuteReader();

            if (reader.Read())
            {
                // Card exists, return it
                double existingUnits = reader.GetDouble(reader.GetOrdinal("Units"));
                int existingRemaining = reader.GetInt32(reader.GetOrdinal("Remaining"));
                long existingTimestamp = reader.GetInt64(reader.GetOrdinal("Timestamp"));
                return new Card(id, existingUnits, existingRemaining, existingTimestamp);
            }
            else
            {
                reader.Close();

                // Card doesn't exist, add it
                string addCardQuery = "INSERT INTO Cards (Id, Units, Remaining, Timestamp) VALUES (@Id, @Units, @Remaining, @Timestamp);";
                SQLiteCommand addCardCommand = new SQLiteCommand(addCardQuery, connection);
                addCardCommand.Parameters.AddWithValue("@Id", id);
                addCardCommand.Parameters.AddWithValue("@Units", units);
                addCardCommand.Parameters.AddWithValue("@Remaining", remaining);
                addCardCommand.Parameters.AddWithValue("@Timestamp", timestamp.Ticks);
                addCardCommand.ExecuteNonQuery();

                return new Card(id, units, remaining, timestamp.Ticks);
