        {
            // Update the card
            string updateCardQuery = "UPDATE Cards SET Units = @Units, Remaining = @Remaining, Timestamp = @Timestamp WHERE Id = @Id;";
            SQLiteCommand updateCardCommand = new SQLiteCommand(updateCardQuery, connection);
            updateCardCommand.Parameters.AddWithValue("@Id", id);
