        static User GetOrAddUser(string id, SQLiteConnection connection)
        {
            // Check if the user exists
            string checkUserQuery = "SELECT * FROM Users WHERE Id = @Id;";
            SQLiteCommand checkUserCommand = new SQLiteCommand(checkUserQuery, connection);
            checkUserCommand.Parameters.AddWithValue("@Id", id);
            SQLiteDataReader reader = checkUserCommand.ExecuteReader();

