            {
                // User exists, return it
                return new User(id);
            }
            else
            {
                reader.Close();

                // User doesn't exist, add it
                string addUserQuery = "INSERT INTO Users (Id) VALUES (@Id);";
                SQLiteCommand addUserCommand = new SQLiteCommand(addUserQuery, connection);
                addUserCommand.Parameters.AddWithValue("@Id", id);
                addUserCommand.ExecuteNonQuery();

                return new User(id);
