        return card_id in self.cards

    def user_has_card(self, user_id, card_id):
        """Check if a user has a card."""
        if user_id in self.users:
            return card_id in self.users[user_id]["cards"]
        return False

    def add_card_to_user(self, user_id, card_id, card_details=None):
        """Add a card to a user."""
        # Check if the user exists
        if user_id not in self.users:
            print(f"User {user_id} does not exist.")
            return False

        # If the card doesn't exist, add it to the card table
        if not self.card_exists(card_id):
            if card_details is None:
                print(f"Card details not provided for new card {card_id}.")
                return False
            self.add_card(card_id, card_details)

        # If the user doesn't have the card, add it to their cards
        if not self.user_has_card(user_id, card_id):
            self.users[user_id]["cards"].add(card_id)
            return True

