#include <iostream>
#include <string>

using namespace std;

// 定义一个链表结构体，用于存储文本编辑器中的字符串
struct Node {
    string text;
    Node* next;
};

class Editor {
public:
    Editor() {
        head = new Node;
        tail = head;
        cursor = head;
        head->next = nullptr;
        pos = 0;
    }

    ~Editor() {
        Node* p = head;
        while (p != nullptr) {
            Node* q = p;
            p = p->next;
            delete q;
        }
    }

    // 增加字符串
    void insert(string s) {
        int len = s.length();
        for (int i = 0; i < len; i++) {
            Node* newNode = new Node;
            newNode->text = s[i];
            newNode->next = cursor->next;
            cursor->next = newNode;
            if (cursor == tail) {
                tail = newNode;
            }
            cursor = newNode;
            pos++;
        }
    }

    // 删除字符串
    void remove() {
        if (cursor == head) {
            return;
        }

        Node* p = head;
        while (p->next != cursor) {
            p = p->next;
        }
        p->next = cursor->next;
        if (cursor == tail) {
            tail = p;
        }
        Node* q = cursor;
        cursor = p;
        delete q;
        pos--;
    }

    // 修改字符串
    void modify(char c) {
        if (cursor == head) {
            return;
        }
        cursor->text = c;
    }

    // 查找字符串
    int search(string s) {
        int len = s.length();
        int cnt = 0;
        Node* p = head->next;
        while (p != nullptr) {
            if (p->text == s[cnt]) {
                cnt++;
            } else {
                cnt = 0;
            }
            if (cnt == len) {
                return pos - cnt + 1;
            }           
            p = p->next;
            pos++;
        }
        return -1;
    }

    // 移动光标
    void move(int n) {
        if (n < 0) {
            for (int i = 0; i < -n; i++) {
                if (cursor == head) {
                    break;
                }
                cursor = head;
                while (cursor->next != tail) {
                    cursor = cursor->next;
                }
                tail = cursor;
            }
        } else if (n > 0) {
            for (int i = 0; i < n; i++) {
                if (cursor == tail) {
                    break;
                }
                cursor = cursor->next;
                pos++;
            }
        }
    }

    // 输出文本编辑器中的内容
    void print() {
        Node* p = head->next;
        while (p != nullptr) {
            cout << p->text;
            p = p->next;
        }
        cout << endl;
    }

private:
    Node* head;
    Node* tail;
    Node* cursor;
    int pos;
};

