    Editor e;
    e.insert("Hello, world!");
    e.print();  // "Hello, world!"
    e.move(6);
    e.insert("C++");
    e.print();  // "Hello, C++world!"
    e.move(-8);
    e.remove();
    e.print();  // "Hello, world!"
    e.modify('?');
