document.addEventListener("DOMContentLoaded", function () {
    const accountingForm = document.getElementById("accounting-form");
    const transactionsTable = document.getElementById("transactions-table");
    const totalAmount = document.getElementById("total-amount");

    let transactions = [];
    let sum = 0;

    accountingForm.addEventListener("submit", function (event) {
        event.preventDefault();

        const description = this.elements["description"].value;
        const amount = parseInt(this.elements["amount"].value);

        transactions.push({
            description: description,
            amount: amount
        });

        renderTransactions();
        this.reset();
    });

    function renderTransactions() {
        const tbody = transactionsTable.tBodies[0];
        tbody.innerHTML = "";

        transactions.forEach(function (transaction, index) {
            const row = tbody.insertRow();

             {هزینه}
            const cellIndex = row.insertCell();
            cellIndex.textContent = index + 1;

            const cellDescription = row.insertCell();
            cellDescription.textContent = transaction.description;

            const cellAmount = row.insertCell();
            cellAmount.textContent = transaction.amount.toLocaleString("fa-IR");

            sum += transaction.amount;
        });

        totalAmount.textContent = sum.toLocaleString("fa-IR");
    }
});
