    } else if (message.content.startsWith('!seek')) {
        if (!serverQueue) return message.reply("There's no song to seek.");
        const time = parseInt(message.content.split(' ')[1]);
        if (!Number.isInteger(time) || time < 0) {
            return message.reply('Invalid time.');
        }
        serverQueue.player.seek(time * 1000);
        return message.channel.send(`Seeked the song to ${time} seconds.`);
    } else if (message.content.startsWith('!forward') || message.content.startsWith('!rewind')) {
        if (!serverQueue) return message.reply("There's no song to seek.");
        const offset = parseInt(message.content.split(' ')[1]);
        if (!Number.isInteger(offset) || offset < 0) {
            return message.reply('Invalid time.');
        }
        const currentPosition = serverQueue.player.playbackDuration / 1000;
        const newPosition = message.content.startsWith('!forward') ? currentPosition + offset : currentPosition - offset;
        if (newPosition > 0) {
            serverQueue.player.seek(newPosition * 1000);
            return message.channel.send(`${message.content.startsWith('!forward') ? 'Forwarded' : 'Rewinded'} the song by ${offset} seconds.`);
        } else {
            return message.reply('Cannot seek to a negative offset.');
        }
    } else if (message.content === '!nowplaying') {
        if (!serverQueue) return message.reply("There's no song playing.");
        return message.channel.send(`Now playing: **${serverQueue.songs[0].title}**`);
    } else if (message.content === '!join') {
        if (!voiceChannel) return message.reply('You have to be in a voice channel to request me to join.');
        if (serverQueue) return message.reply('I am already in the voice channel.');
        joinVoiceChannel({
            channelId: voiceChannel.id,
            guildId: voiceChannel.guild.id,
            adapterCreator: voiceChannel.guild.voiceAdapterCreator
        });
        return message.channel.send('Joined the voice channel.');
    } else if (message.content === '!leave') {
        if (!voiceChannel) return message.reply('You have to be in a voice channel to request me to leave.');
        if (!serverQueue) return message.reply("I'm not in a voice channel.");
        serverQueue.connection.destroy();
        queue.delete(message.guild.id);
        return message.channel.send('Left the voice channel.');
    }
});

async function play(guildId, song) {
    const queueBound = queue.get(guildId);
    if (!song) {
        queueBound.connection.destroy();
        queue.delete(guildId);
        return;
    }

    queueBound.connection.subscribe(queueBound.player);
    queueBound.player.play(song.resource);

    queueBound.player.on(AudioPlayerStatus.Idle, async () => {
        queueBound.songs.shift();
        play(guildId, queueBound.songs[0]);
    });

    try {
        await entersState(queueBound.connection, VoiceConnectionStatus.Ready, 15e3);
    } catch (error) {
        console.error(error);
        queue.delete(guildId);
        queueBound.connection.destroy();
    }
}

