        enabledByDefault = false
)
public class ClanChatAssistantPlugin extends Plugin {

    private String clanChatName;
    private String[] messages;
    private String[] userAccounts;
    private int accountIndex;
    private boolean isJoined;

    @Inject
    private Client client;

    @Inject
    private ClanChatAssistantConfig config;

    @Inject
    private OverlayManager overlayManager;

    @Inject
    private ClanChatAssistantOverlay overlay;

    @Override
    protected void startUp() throws Exception {
        overlayManager.add(overlay);
    }

    @Override
    protected void shutDown() throws Exception {
        overlayManager.remove(overlay);
    }

    @Subscribe
    public void onGameTick(GameTick tick) {
        if (!isJoined) {
            joinClanChat();
        } else {
            sendMessages();
        }
    }

    @Subscribe
    public void onChatMessage(ChatMessage chatMessage) {
        if (chatMessage.getType() == ChatMessageType.GAMEMESSAGE) {
            switch (chatMessage.getMessage()) {
                case "<col=7f007f>You are not currently in a clan chat.</col>":
                case "<col=7f007f>You have been kicked from the clan chat.</col>":
                    logout();
                    accountIndex++;
                    if (accountIndex >= userAccounts.length) {
                        accountIndex = 0;
                    }
                    isJoined = false;
                    break;
            }
        }
    }

    private void joinClanChat() {
        if (isCurrentScreenValid()) {
            client.invoke(joinClanChatOption(clanChatName));
            isJoined = true;
        }
    }

    private void sendMessages() {
        for (String message : messages) {
            client.invoke(sendMessageOption(message));
        }
    }

    private void logout() {
        client.logout();
        try {
            Thread.sleep(2000);
        } catch (InterruptedException ex) {
            Thread.currentThread().interrupt();
        }
        client.setUsername(userAccounts[accountIndex]);
        client.setPassword(config.getPassword());
        client.login();
    }

    private boolean isCurrentScreenValid() {
        return client.getVar(WidgetID.CLAN_CHAT_GROUP_ID, 36) == 0;
    }

    private static String joinClanChatOption(String clanChatName) {
        return "Join Chat";
    }

    private static String sendMessageOption(String message) {
        return message;
    }

