```cpp
#include <iostream>
using namespace std;

int factorial(int n) {
    int res = 1;
    for (int i = 1; i <= n; i++) {
        res *= i;
    }
    return res;
}

int main() {
    int n;
    cout << "请输入一个正整数n：";
    cin >> n;
    cout << n << "! = " << factorial(n) << endl;
    return 0;
}
