    }

    output_file.close();
  }

 private:
  double calculateDerivativeTwoPoints(double x, double step) {
    return (func_(x + step) - func_(x)) / step;
  }

  double calculateDerivativeThreePoints(double x, double step) {
    return (func_(x + step) - func_(x - step)) / (2 * step);
  }

  Function func_;
  double left_boundary_ = 0;
  double right_boundary_ = 1;
  int partitions_ = 100;
  bool three_points_method_ = true;
};

int main() {
  DerivativeCalculator calculator;

  calculator.setFunction([](double x) { return x * x; });
  calculator.setBoundaries(0, 10);
  calculator.setPartitions(20);
  calculator.setCalculationMethod(true);

  std::vector<double> res = calculator.calculateDerivative();
  calculator.printDerivative(res, "results.txt");

  return 0;
}
