#define ALPHABET_SIZE 26

const int T9_KEYS[] = {2, 2, 2, 3, 3, 3, 4, 4, 4, 5, 5, 5, 6, 6, 6, 7, 7, 7, 7, 8, 8, 8, 9, 9, 9, 9};

typedef struct trie_node
{
    struct trie_node *children[ALPHABET_SIZE];
    bool is_word_end;
} TrieNode;

// Get a new TrieNode
TrieNode *get_node()
{
    TrieNode *new_node = (TrieNode *)malloc(sizeof(TrieNode));
    new_node->is_word_end = false;

    for (int i = 0; i < ALPHABET_SIZE; i++)
        new_node->children[i] = NULL;

    return new_node;
}

// Insert a word into trie
void insert(TrieNode *root, const char *word)
{
    TrieNode *curr = root;
    for (int i = 0; i < strlen(word); i++)
    {
        int index = tolower(word[i]) - 'a';
        if (!curr->children[index])
            curr->children[index] = get_node();

        curr = curr->children[index];
    }
    curr->is_word_end = true;
}

// Helper function to display words
void display_helper(TrieNode *node, char *word, int level)
{
    if (node->is_word_end)
    {
        word[level] = '\0';
        printf("%s ", word);
    }

    for (int i = 0; i < ALPHABET_SIZE; i++)
    {
        if (node->children[i])
        {
            word[level] = i + 'a';
            display_helper(node->children[i], word, level + 1);
        }
    }
}

// Display words from trie
void display(TrieNode *root, const char *input)
{
    char word[32];
    TrieNode *curr = root;
    for (int i = 0; i < strlen(input); i++)
    {
        int key = input[i] - '0';
        int sub_key = tolower(input[i]) - 'a';
        if (key >= 2 && key <= 9)
        {
            int index = (key - 2) * 3 + (tolower(input[i + 1]) == input[i] ? 1 : 0) + (tolower(input[i + 2]) == input[i] ? 1 : 0);
            if (!curr->children[index])
                return;

            curr = curr->children[index];
            i++; // Consume next character if required
        }
        else if (sub_key >= 0 && sub_key < ALPHABET_SIZE)
        {
            if (!curr->children[sub_key])
                return;

            curr = curr->children[sub_key];
        }
    }
    display_helper(curr, word, 0);
    printf("\n");
}

// Load dictionary from file
