    double sin_2M = sin(DEG_TO_RAD * (2 * M));
    double sin_3M = sin(DEG_TO_RAD * (3 * M));
    double C = (1.914 * sin_M) + (0.020 * sin_2M) + (0.0003 * sin_3M);
    double sun_long = L0 + C;
    double v = M + C + 102.937;
    double R = 1.00014 - (0.01671 * cos(DEG_TO_RAD * (M + C))) + (0.00014 * cos(DEG_TO_RAD * (2 * M + 2 * C)));
    n = fix_hour(n - R + 0.00569 - (0.004178 * sin(DEG_TO_RAD * v)));

    double w = 125.04 - (1934.136 * t);
    double epsilon = 23.439 - (0.00000036 * t);
    double alpha = atan2(cos(DEG_TO_RAD * epsilon) * sin(DEG_TO_RAD * sun_long), cos(DEG_TO_RAD * sun_long)) * RAD_TO_DEG;

    sin_M = sin(DEG_TO_RAD * sun_long * -0.985612278);
    cos_M = cos(DEG_TO_RAD * (11.25386278 + sun_long));
    *decl = asin(sin(DEG_TO_RAD * epsilon) * sin_M) * RAD_TO_DEG;

