double calc_eq_of_time(double n, double t) {
    return n + 220916 - t - (0.005610 * sin(DEG_TO_RAD * (n - 1))) - (0.045454 * sin(DEG_TO_RAD * (2 * (n - 1))));
}

double calc_asr(int step, double lat, double decl) {
    double d = -atan(1 / (1 + tan((lat - decl) * DEG_TO_RAD)));
    return (step + d) / 15.0;
}

void get_prayer_times(int year, int month, int day, double lat, double lng,
