#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

// Функция бинарного поиска
int binarySearch(std::vector<std::string>& list, std::string value) {
    int left = 0;
    int right = list.size() - 1;

    // Делаем поиск, пока левая граница не станет больше правой
    while (left <= right) {
        // Находим средний элемент в текущем отрезке
        int middle = (left + right) / 2;

        // Если средний элемент равен искомому значению, возвращаем его индекс
        if (list[middle] == value)
            return middle;

        // Если значение среднего элемента меньше искомого значения,
        // значит искомое значение находится в правой половине, иначе в левой
        if (list[middle] < value)
            left = middle + 1;
        else
            right = middle - 1;
    }

    // Если значение не найдено, возвращаем -1
    return -1;
}




