void Exchange::PrintUserPortfolios(std::ostream &os) const {
  os << std::endl << "---------- User Portfolios ----------" << std::endl;

  std::vector<UserAccount> accounts;
  for (auto it = user_accounts_.begin(); it != user_accounts_.end(); ++it) {
    accounts.push_back(it->second);
  }

  std::sort(accounts.begin(), accounts.end(),
            [](UserAccount a, UserAccount b) { return a.GetUsername() < b.GetUsername(); });

  for (auto it = accounts.begin(); it != accounts.end(); ++it) {
    os << it->GetUsername() << ": " << std::endl;
    it->PrintPortfolio(os);
  }

  os << "-------------------------------------" << std::endl;
}
