    for(i=0;i<NODE;i++)
    {
        dist[i] = maps[start][i];//初始化dist数组
    }
    dist[start] = 0;
    visit[start] = 1;//将起点标记为已访问
    for(i=0;i<NODE;i++)//循环node次来找到最短路径 
    {
        int  u = -1;
        for(j=0;j<NODE;j++)
        {
            if (!visit[j] && (u == -1 || dist[j] < dist[u]))//找到未访问的离start最近的节点 
            {
                u = j;
            }
        }
        visit[u] = 1;
        for(int v = 0;v < NODE ;v++)
        {
            if(maps[u][v]!=INT_MAX&&dist[u]+maps[u][v]<dist[v])
            {
                dist[v] = dist[u] + maps[u][v];//更新dist[v]的数据 
                prev[v]=u;//将u记为v的前驱 
