You can modify the program as follows to use `std::string` instead of `std::vector<char>`:

```
#include <iostream>
#include <string>
#include <vector>
#include <algorithm>
using namespace std;

bool solve(string& mass, int pos, vector<int>& memo) {
    if (mass.find('X') == string::npos) {
        return true;
    }
    if (pos == mass.size() - 1) {
        return true;
    }
    if (memo[pos] != -1) {
        return memo[pos] == 1;
    }
    bool boolik = false;
    if (mass[pos] == '7') {
        // try moves to the left over ''?''
        for (int i = pos - 1; i >= 0 && mass[i] != 'X'; i--) {
            if (mass[i] == '.') {
                mass[pos] = '.';
                mass[i] = '7';
                boolik |= solve(mass, i, memo);
                mass[i] = '.';
                mass[pos] = '7';
            }
        }
        // try moves to the right over adjacent dots
        for (int i = pos + 1; i < mass.size() && mass[i] != 'X'; i++) {
            if (mass[i] == '.') {
                mass[pos] = '.';
                mass[i] = '7';
                boolik |= solve(mass, i, memo);
                mass[i] = '.';
                mass[pos] = '7';
            }
        }
    }
    memo[pos] = boolik ? 1 : 0;
    return boolik;
}

int main() {
    int n;
    cin >> n;
    string s;
    cin >> s;
    vector<int> memo(n, -1);
    int start = s.find('X');
    if (start == n - 1) {
        cout << "YES" << endl;
    } else if (start != string::npos && solve(s, start, memo)) {
        cout << "YES" << endl;
    } else {
        cout << "NO" << endl;
    }
    return 0;
}
