1. [Java Native Interface (JNI)](https://docs.oracle.com/en/java/javase/14/docs/specs/jni/index.html)
2. [Java JNI调用C代码步骤详解](https://blog.csdn.net/fanfeilong_wf/article/details/85398660)

//海龟策略EA代码
//Author: Jack
//Date: 2021-07-08

//参数定义
input int N = 20; //市场上涨/下跌过去N个交易日的最高/最低点
input double ATRMultiple1 = 1.0; //第一个买卖点位的ATR倍数
input double ATRMultiple2 = 1.5; //第二个买卖点位的ATR倍数
input double ATRMultiple3 = 2.0; //第三个买卖点位的ATR倍数
input double ATRMultiple4 = 2.5; //第四个买卖点位的ATR倍数
input int stopLossN = 10; //交易中止时，卖出头寸的天数

//全局变量定义
double lastBuyPrice = 0; //上一个买入价格
double lastSellPrice = 0; //上一个卖出价格
bool isLong = false; //是否持有多头头寸
bool isShort = false; //是否持有空头头寸
int positions = 0; //当前头寸数

//入口函数OnInit
void OnInit()
{
   //关注四个品种的行情
   SymbolSelect("EURUSD", true);
   SymbolSelect("GBPUSD", true);
   SymbolSelect("USDJPY", true);
   SymbolSelect("AUDUSD", true);

   //设置EA的参数
   Comment("Turtle EA: N = ", N, ", ATRMultiple1 = ", ATRMultiple1, ", ATRMultiple2 = ", ATRMultiple2, ", ATRMultiple3 = ", ATRMultiple3, ", ATRMultiple4 = ", ATRMultiple4, ", Stop Loss N = ", stopLossN);
}

//入口函数OnTick
void OnTick()
{
   for(int i=0; i<SymbolsTotal(); i++)
   {
      const string symbol = SymbolName(i, false);

      //获取ATR值
      double atr = iATR(symbol, PERIOD_D1, 14, 0);

      //获取过去N个交易日的最高和最低价格
      double highestHigh = iHigh(symbol, PERIOD_D1, MODE_HIGH, N, 0);
      double lowestLow = iLow(symbol, PERIOD_D1, MODE_LOW, N, 0);

      //获取当前价格
      double currentPrice = SymbolInfoDouble(symbol, SYMBOL_BID);

      //进行交易判断
      if(currentPrice > highestHigh + ATRMultiple1 * atr)
      {
         //买进头寸
         if(!isLong && positions < 4)
         {
            lastBuyPrice = currentPrice;
            OrderSend(symbol, OP_BUY, 1, currentPrice, 10, currentPrice - atr * 2, currentPrice + atr * 4, "Turtle Buy", 0, 0, Green);
            isLong = true;
            positions++;
         }
         else if(isLong && positions < 4)
         {
            lastBuyPrice = currentPrice;
            OrderSend(symbol, OP_BUY, 1, currentPrice, 10, currentPrice - atr * 2, currentPrice + atr * 4, "Turtle Buy", 0, 0, Green);
            positions++;
         }
      }
      else if(currentPrice < lowestLow - ATRMultiple1 * atr)
      {
         //卖出头寸
         if(!isShort && positions > -4)
         {
            lastSellPrice = currentPrice;
            OrderSend(symbol, OP_SELL, 1, currentPrice, 10, currentPrice + atr * 2, currentPrice - atr * 4, "Turtle Sell", 0, 0, Red);
            isShort = true;
            positions--;
         }
         else if(isShort && positions > -4)
         {
            lastSellPrice = currentPrice;
            OrderSend(symbol, OP_SELL, 1, currentPrice, 10, currentPrice + atr * 2, currentPrice - atr * 4, "Turtle Sell", 0, 0, Red);
            positions--;
         }
      }

      //添加头寸
      if(isLong && positions < 4)
      {
         if(currentPrice > lastBuyPrice + ATRMultiple2 * atr && positions < 3)
         {
            lastBuyPrice = currentPrice;
            OrderSend(symbol, OP_BUY, 1, currentPrice, 10, currentPrice - atr * 2, currentPrice + atr * 4, "Turtle Buy", 0, 0, Green);
            positions++;
         }
         else if(currentPrice > lastBuyPrice + ATRMultiple3 * atr && positions < 2)
         {
            lastBuyPrice = currentPrice;
            OrderSend(symbol, OP_BUY, 1, currentPrice, 10, currentPrice - atr * 2, currentPrice + atr * 4, "Turtle Buy", 0, 0, Green);
            positions++;
         }
         else if(currentPrice > lastBuyPrice + ATRMultiple4 * atr && positions < 1)
         {
            lastBuyPrice = currentPrice;
            OrderSend(symbol, OP_BUY, 1, currentPrice, 10, currentPrice - atr * 2, currentPrice + atr * 4, "Turtle Buy", 0, 0, Green);
            positions++;
         }
      }
      else if(isShort && positions > -4)
      {
         if(currentPrice < lastSellPrice - ATRMultiple2 * atr && positions > -3)
         {
            lastSellPrice = currentPrice;
            OrderSend(symbol, OP_SELL, 1, currentPrice, 10, currentPrice + atr * 2, currentPrice - atr * 4, "Turtle Sell", 0, 0, Red);
            positions--;
         }
         else if(currentPrice < lastSellPrice - ATRMultiple3 * atr && positions > -2)
         {
            lastSellPrice = currentPrice;
            OrderSend(symbol, OP_SELL, 1, currentPrice, 10, currentPrice + atr * 2, currentPrice - atr * 4, "Turtle Sell", 0, 0, Red);
            positions--;
         }
         else if(currentPrice < lastSellPrice - ATRMultiple4 * atr && positions > -1)
         {
            lastSellPrice = currentPrice;
            OrderSend(symbol, OP_SELL, 1, currentPrice, 10, currentPrice + atr * 2, currentPrice - atr * 4, "Turtle Sell", 0, 0, Red);
            positions--;
         }
      }

      //交易中止
      if(positions == 0 && (isLong || isShort))
      {
         //获取过去stopLossN个交易日的最低价
         double lowestLow = iLow(symbol, PERIOD_D1, MODE_LOW, stopLossN, 0);

         if(isLong)
         {
            OrderSend(symbol, OP_SELL, positions, currentPrice, 10, lowestLow, currentPrice + atr * 4, "Turtle Stop Loss", 0, 0, Red);
         }
         else
         {
            OrderSend(symbol, OP_BUY, positions, currentPrice, 10, currentPrice - atr * 4, lowestLow, "Turtle Stop Loss", 0, 0, Green);
         }

         //重置变量
         lastBuyPrice = 0;
         lastSellPrice = 0;
         isLong = false;
         isShort = false;
         positions = 0;
      }
   }
}

