```cpp
#include <iostream>

using namespace std;

// Função Limpar_Consola
void Limpar_Consola() {
    for (int i = 0; i < 50; ++i) {
        cout << endl;
    }
}

int main() {
    // Imprime uma mensagem na tela
    cout << "Primeira linha antes de limpar o console" << endl;

    // Chama a função Limpar_Consola
    Limpar_Consola();

    // Imprime outra mensagem na tela após limpar o console
    cout << "Primeira linha apos limpar o console" << endl;

    return 0;
}
```

