```javascript
// La función 'mergeSort' toma un array como argumento y devuelve la versión ordenada de ese array.
function mergeSort(array) {
  // Si la longitud del array es igual o menor a 1, se devuelve el mismo array, porque no hay nada que ordenar.
  if (array.length <= 1) {
    return array;
  }

  // Obtiene el índice medio del array y lo guarda en la variable 'mitad'
  let mitad = parseInt(array.length / 2);
  // Divide el array en dos partes, izquierda y derecha, con base en la variable 'mitad'
  let izq = mergeSort(array.slice(0, mitad));
  let der = mergeSort(array.slice(mitad));

  // La función 'merge' combina ambas partes izquierda y derecha, y devuelve un array ordenado.
  let merge = function (izq, der) {
    let datos = [];

    // Este bucle se ejecutará hasta que las longitudes de ambos arrays izquierda y derecha sean mayores a 0.
    while (izq.length > 0 && der.length > 0) {
      // Si el primer elemento del array izquierdo es menor o igual al primer elemento del array derecho, se elimina ese elemento del array izquierdo y se añade al array 'datos', de lo contrario, se hace lo mismo con el array derecho.
      datos.push(izq[0] <= der[0] ? izq.shift() : der.shift());
    }

    // Concatena los arrays izquierda y derecha al array 'datos' y devuelve el resultado.
    return datos.concat(izq).concat(der);
  };
  // Llama a la función 'merge' con los arrays izquierda y derecha como argumentos y devuelve el resultado.
  return merge(izq, der);
}
```
