			double Rt = sqrt(AzmRg * AzmRg + shared_Rg[threadIdx.x] * shared_Rg[threadIdx.x] + HH * HH);
			double tau = 2.0 * Rt / ccc;
			int  nr = round((tau - trs[0]) * L * Fr);
			nr = nr < (int)L* (int)Na ? nr : (int)L * (int)Na;
