			phi.y = __sinf(a);
			d_b.x = rd.x * phi.x - rd.y * phi.y;
			d_b.y = rd.x * phi.y + rd.y * phi.x;
			sum.x += d_b.x;
			sum.y += d_b.y;
		}
	}
	SAR[row * Za + col].x = sum.x;
	SAR[row * Za + col].y = sum.y;

