using System.Collections.Generic;
using UnityEngine;
using UnityEngine.UI;
using DG.Tweening;
using Random = UnityEngine.Random;

public class ButtonMover : MonoBehaviour
{
    public Transform[] buttons; 
    public float moveTime = 1f; 
    public float flushTime = 5f; 
    private bool isPlaying = false;
    private int totalTweens = 0;

    private float minX, maxX, minY, maxY;
    private Vector2 randomPosition;

    void Update()
    {
        if (!isPlaying)
        {
            isPlaying = true;
            foreach (var button in buttons)
                if (Random.Range(0, 2) == 0) MoveRandom(button);
        }

        if (Time.frameCount % 30 == 0)
        {
            int newTotalTweens = DOTween.TotalPlayingTweens();
            if (newTotalTweens != totalTweens)
            {
                totalTweens = newTotalTweens;
                Debug.Log("Active Tweens: " + totalTweens);
                if (totalTweens == 0) isPlaying = false;
            }
        }
    }

    private void FlushButtons()
    {
        List<int> usedButtonIndices = new List<int>();
        foreach (var t in buttons)
        {
            Transform otherButton;
            do { otherButton = buttons[Random.Range(0, buttons.Length)]; }
            while (otherButton == t || usedButtonIndices.Contains(otherButton.GetSiblingIndex()));
            usedButtonIndices.Add(otherButton.GetSiblingIndex());
            t.DOMove(otherButton.position, flushTime).SetEase(Ease.InOutSine).SetId(t);
        }
    }

    private void MoveRandom(Transform button)
    {
        AudioSource audioSource = button.GetComponent<AudioSource>();
        float screenHeight = Camera.main.orthographicSize * 2;
        float screenWidth = screenHeight * Screen.width / Screen.height;
        float buttonWidth = button.GetComponent<SpriteRenderer>().bounds.size.x;
        float buttonHeight = button.GetComponent<SpriteRenderer>().bounds.size.y;
        minX = -screenWidth / 2 + buttonWidth / 2;
        maxX = screenWidth / 2 - buttonWidth / 2;
        minY = -screenHeight / 2 + buttonHeight / 2;
        maxY = screenHeight / 2 - buttonHeight / 2;
        float randomX = Random.Range(minX, maxX);
        float randomY = Random.Range(minY, maxY);
        randomPosition = new Vector2(randomX, randomY);
        randomPosition.x = Mathf.Clamp(randomPosition.x, -screenWidth / 2 , screenWidth / 2 );
        randomPosition.y = Mathf.Clamp(randomPosition.y, -screenHeight / 2 , screenHeight / 2 );
        Ease easeRandom = (Ease) Random.Range(0, 30);
        Ease easeRandom2 = (Ease) Random.Range(0, 30);
        Sequence sequence = DOTween.Sequence();
        sequence.Append(button.DOMove(randomPosition, moveTime).SetEase(easeRandom).SetId(button));
        sequence.Join(button.DORotate(new Vector3(0, 360, 0), moveTime, RotateMode.FastBeyond360)
            .SetEase(easeRandom2).SetId(button))
            .OnPlay(() => audioSource.Play())
            .OnComplete(() => audioSource.Stop());
    }
}

