public static async Task<string?> ShowOtpDialog()
{
    var taskCompletionSource = new TaskCompletionSource<string?>();
    using var layoutInflater = LayoutInflater.FromContext(Instance)!;
    using var view = layoutInflater.Inflate(Resource.Layout.OtpDialog, null);

    var otpEditText = view!.FindViewById<EditText>(Resource.Id.dialog_input)!;
