        .SetTitle("Telegram verification code")
        .SetView(view)
        .SetCancelable(true); // Change this to true to allow user to dismiss the dialog

    AlertDialog? dialog = null;

    submitButton.Click += (_, _) =>
    {
        dialog?.Dismiss();
        var otp = otpEditText.Text;
        taskCompletionSource.SetResult(otp);
    };

    await MainThread.InvokeOnMainThreadAsync(() =>
    {
        dialog = dialogBuilder.Create();
        dialog.Show();
    }).ConfigureAwait(false);

    // Handle dialog dismiss event
    dialog.Cancel += (_, _) =>
    {
        // Set a default result (e.g., null) when the user dismisses the dialog
        taskCompletionSource.TrySetResult(null);
    };

    // Wait for the task to be completed
    var task = await taskCompletionSource.Task.ConfigureAwait(false);

    // Dispose
    otpEditText.Dispose();
    submitButton.Dispose();
    dialog?.Dispose();
    return task;
}
