private static Vector3 ClosestPointOnSegment(Vector3 start, Vector3 end, Vector3 position)
{
    Vector3 direction = end - start;
    float length = direction.magnitude;

    if (length == 0f)
    {
        return start;
    }

    direction /= length;

    float distanceAlongLine = Vector3.Dot(position - start, direction);
    distanceAlongLine = Mathf.Clamp(distanceAlongLine, 0f, length);

    return start + direction * distanceAlongLine;
}

