public static Vector3 ClosestPointOnSpline(Vector3[] spline, Vector3 position)
{
    int segmentCount = spline.Length - 1;
    float segmentLength = GetSplineLength(spline) / segmentCount;

    int closestSegmentIndex = FindClosestSegmentIndex(spline, position, segmentLength);

    Vector3 closestPoint = ClosestPointOnSegment(spline[closestSegmentIndex], spline[closestSegmentIndex + 1], position);

    return closestPoint;
}

private static int FindClosestSegmentIndex(Vector3[] spline, Vector3 position, float segmentLength)
{
    int minIndex = 0;
    int maxIndex = spline.Length - 2;
    int closestSegmentIndex = 0;

    while (minIndex <= maxIndex)
    {
        int midIndex = (minIndex + maxIndex) / 2;
        float midPointDistance = DistanceFromStartOfSpline(spline, midIndex, segmentLength, position);

        if (midPointDistance < 0)
        {
            closestSegmentIndex = midIndex;
            maxIndex = midIndex - 1;
        }
        else
        {
            minIndex = midIndex + 1;
        }
    }

    return closestSegmentIndex;
}

private static float DistanceFromStartOfSpline(Vector3[] spline, int segmentIndex, float segmentLength, Vector3 position)
{
    int segmentStartIndex = segmentIndex;
    int segmentEndIndex = segmentIndex + 1;

    Vector3 segmentStartPoint = spline[segmentStartIndex];
    Vector3 segmentEndPoint = spline[segmentEndIndex];

    float segmentStartDistance = segmentLength * segmentStartIndex;
    float segmentEndDistance = segmentLength * segmentEndIndex;

    float distanceAlongLine = Vector3.Dot(position - segmentStartPoint, segmentEndPoint - segmentStartPoint) / (segmentEndPoint - segmentStartPoint).sqrMagnitude;

    if (distanceAlongLine < 0)
    {
        return segmentStartDistance;
    }
    else if (distanceAlongLine > 1)
    {
        return segmentEndDistance;
    }
    else
    {
        return segmentStartDistance + distanceAlongLine * (segmentEndDistance - segmentStartDistance);
    }
}

private static float GetSplineLength(Vector3[] spline)
{
    float length = 0;

    for (int i = 1; i < spline.Length; i++)
    {
        length += Vector3.Distance(spline[i], spline[i - 1]);
    }

    return length;
}

