  <script>
    // 检查输入的浮点数是否为有效数字
    function isFloat(n) {
      return Number(n) === n && n % 1 !== 0;
    }

    // 检查输入的内存地址是否为有效的16进制数
    function isHex(s) {
      return /[1]+$/.test(s) && s.length % 2 == 0;
    }

    // 显示转换结果
    function showResult(result) {
      var resultDiv = document.querySelector(".result");
      if (!resultDiv) {
        resultDiv = document.createElement("div");
        resultDiv.className = "result";
        document.querySelector(".container").appendChild(resultDiv);
      }
      resultDiv.innerHTML = "<p>" + result + "</p>";
    }

    // 处理表单提交事件
    var form = document.querySelector("form");
    form.addEventListener("submit", function(event) {
      event.preventDefault();
      var floatNum = form.querySelector("#float_num").value.trim();
      var action = event.target.elements["action"].value;
      if (action == "ftoi") {
        if (isFloat(floatNum)) {
          var xhr = new XMLHttpRequest();
          xhr.open("POST", "/");
          xhr.setRequestHeader("Content-Type", "application/x-www-form-urlencoded");
          xhr.onreadystatechange = function() {
            if (xhr.readyState == XMLHttpRequest.DONE) {
              if (xhr.status == 200) {
                var result = xhr.responseText;
                showResult(result);
              } else {
                showResult("Error: " + xhr.statusText);
              }
            }
          };
          xhr.send("float_num=" + floatNum + "&action=ftoi");
        } else {
          showResult("Error: Invalid float number");
        }
      } else if (action == "itof") {
        var intNum = form.querySelector("#int_num").value.trim();
        if (isHex(intNum)) {
          var xhr = new XMLHttpRequest();
          xhr.open("POST", "/");
          xhr.setRequestHeader("Content-Type", "application/x-www-form-urlencoded");
          xhr.onreadystatechange = function() {
            if (xhr.readyState == XMLHttpRequest.DONE) {
              if (xhr.status == 200) {
                var result = parseFloat(xhr.responseText).toFixed(2);
                showResult(result);
              } else {
                showResult("Error: " + xhr.statusText);
              }
