    VerifyPop
  },
  data(){
    return {
      email: '',
      password: '',
      verifyCode: ''
    }
  },
  methods: {
    onSubmit(){
      // 验证码验证逻辑
      const code = VerifyPop.code;
      if(code === this.verifyCode){
        // 验证通过
        // 发送表单数据到服务器
        // …
      } else {
        // 验证未通过
        alert('验证码输入错误，请重新输入')
      }
    }
  }
}
</script>
```

