    private IEnumerator JumpHeadBob()
    {
        float timeElapsed = 0;
        Vector3 initialCameraPosition = playerCamera.transform.localPosition;

        while (timeElapsed < headBobDuration)
        {
            float yOffset = Mathf.Sin(timeElapsed / headBobDuration * Mathf.PI) * headBobHeightAmplitude;
            playerCamera.transform.localPosition = new Vector3(initialCameraPosition.x, initialCameraPosition.y + yOffset, initialCameraPosition.z);

            float zRotation = Mathf.Sin(timeElapsed / headBobDuration * Mathf.PI * 2) * headBobRotationAmplitude;

            // Get the current X rotation of the camera.
            float xRotation = playerCamera.transform.localRotation.eulerAngles.x;

            // Apply the updated rotation.
            playerCamera.transform.localRotation = Quaternion.Euler(xRotation, 0, zRotation);

            timeElapsed += Time.deltaTime;
            yield return null;
        }

        playerCamera.transform.localPosition = initialCameraPosition;
    }

