import React, { useState } from 'react';
import TaskContainer from './components/TaskContainer';
import SubtaskModal from './components/SubtaskModal';
import { v4 as uuidv4 } from 'uuid';

function App() {
  const [tasks, setTasks] = useState([]);
  const [showSubtaskModal, setShowSubtaskModal] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);

  const handleAddTask = (title, type) => {
    const newTask = {
      id: uuidv4(),
      title,
      type,
      subtasks: [],
    };
    setTasks([...tasks, newTask]);
  };

  const handleDeleteTask = (taskId) => {
    setTasks(tasks.filter((task) => task.id !== taskId));
  };

  const handleShowSubtaskModal = (taskId) => {
    setSelectedTask(tasks.find((task) => task.id === taskId));
    setShowSubtaskModal(true);
  };

  const handleHideSubtaskModal = () => {
    setSelectedTask(null);
    setShowSubtaskModal(false);
  };

  const handleAddSubtask = (taskId, subtask) => {
    const updatedTasks = tasks.map((task) => {
      if (task.id === taskId) {
        const updatedSubtasks = [...task.subtasks, { ...subtask, id: uuidv4() }];
        return { ...task, subtasks: updatedSubtasks };
      }
      return task;
    });
    setTasks(updatedTasks);
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="mb-6 text-3xl font-bold">Task Logger</h1>
      <div className="flex justify-end mb-6">
        <button onClick={() => handleShowSubtaskModal(null)} className="px-4 py-2 bg-blue-500 text-white rounded-md">
          Add Task
        </button>
      </div>
      <TaskContainer tasks={tasks} onTaskDelete={handleDeleteTask} onShowSubtaskModal={handleShowSubtaskModal} />
      {showSubtaskModal && <SubtaskModal task={selectedTask} onCloseModal={handleHideSubtaskModal} onAddSubtask={handleAddSubtask} />}
    </div>
  );
}

