// App
import React from 'react';
import TaskContainer from './components/TaskContainer';
import { TaskProvider } from './TaskContext';

function App() {
  return (
    <TaskProvider>
      <div className="container mx-auto p-6">
        <h1 className="mb-6 text-3xl font-bold">Task Logger</h1>
        <TaskContainer />
      </div>
    </TaskProvider>
  );
