export default App;

//TaskContext

import React, { createContext, useEffect, useState } from 'react';

const TaskContext = createContext();

const TaskProvider = ({ children }) => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const storedTasks = JSON.parse(localStorage.getItem('tasks'));
    if (storedTasks) {
      setTasks(storedTasks);
    }
  }, []);

  useEffect(() => {
   localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  const addTask = (task) => {
    setTasks([...tasks, task]);
  };

  const deleteTask = (taskId) => {
    setTasks(tasks.filter((task) => task.id !== taskId));
  };

  const addSubtask = (taskId, subtask) => {
    const updatedTasks = tasks.map((task) => {
      if (task.id === taskId) {
        const updatedSubtasks = [...task.subtasks, subtask];
        return { ...task, subtasks: updatedSubtasks };
      }
      return task;
    });
    setTasks(updatedTasks);
  };

const updateSubtaskStatus = (taskId, subtaskId, status) => {
  const updatedTasks = tasks.map((task) => {
    if (task.id === taskId) {
      const updatedSubtasks = task.subtasks.map((subtask) => {
        if (subtask.id === subtaskId) {
          return { ...subtask, status };
        }
        return subtask;
      });
      return { ...task, subtasks: updatedSubtasks };
    }
    return task;
  });
  setTasks(updatedTasks);
};

  return (
    <TaskContext.Provider value={{ tasks, addTask, deleteTask, addSubtask, updateSubtaskStatus }}>
      {children}
    </TaskContext.Provider>
  );
};

export { TaskContext, TaskProvider }; 

// TaskContainer
import React, { useContext, useState } from 'react';
import TaskInput from './TaskInput';
import Task from './Task';
import SubtaskModal from './SubtaskModal';
import { TaskContext } from '../TaskContext';

function TaskContainer() {
  const { tasks, addTask, deleteTask, addSubtask } = useContext(TaskContext);
  const [showSubtaskModal, setShowSubtaskModal] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);

  const frontEndTasks = tasks.filter((task) => task.type === 'front-end');
  const backEndTasks = tasks.filter((task) => task.type === 'back-end');

  const handleShowSubtaskModal = (taskId) => {
    setSelectedTask(tasks.find((task) => task.id === taskId));
    setShowSubtaskModal(true);
  };

  const handleHideSubtaskModal = () => {
    setSelectedTask(null);
    setShowSubtaskModal(false);
  };

  return (
    <div>
      <TaskInput onAddTask={addTask} />
      <div className="flex justify-between">
        <div className="w-1/2 mr-4">
          <h2 className="mb-2 font-bold text-lg">Front-End Tasks</h2>
          {frontEndTasks.map((task) => (
            <Task
              key={task.id}
              task={task}
              onTaskDelete={deleteTask}
              onShowSubtaskModal={handleShowSubtaskModal}
            />
          ))}
        </div>
        <div className="w-1/2">
          <h2 className="mb-2 font-bold text-lg">Back-End Tasks</h2>
          {backEndTasks.map((task) => (
            <Task
              key={task.id}
              task={task}
              onTaskDelete={deleteTask}
              onShowSubtaskModal={handleShowSubtaskModal}
            />
          ))}
        </div>
      </div>
      {showSubtaskModal && (
        <SubtaskModal
          key={selectedTask.id} // helps React to identify when the component should be updated.
          task={selectedTask}
          onCloseModal={handleHideSubtaskModal}
          onAddSubtask={addSubtask}
        />
      )}
    </div>
  );
}

export default TaskContainer;

