import React from 'react';

function Task({ task, onTaskDelete, onShowSubtaskModal }) {
  const getProgressBarColor = (averageStatus) => {
    if (averageStatus === 100) {
      return 'bg-green-500';
    } else if (averageStatus >= 50) {
      return 'bg-yellow-500';
    } else {
      return 'bg-red-500';
    }
  };

  const handleDeleteClick = (event) => {
    event.stopPropagation(); // Add this line to stop event propagation
    onTaskDelete(task.id);
  };

  // Add this event handler to handle task click
  const handleTaskClick = () => {
    onShowSubtaskModal(task.id);
  };

  const subtaskStatuses = task.subtasks.map((subtask) => subtask.status);
  const averageStatus = subtaskStatuses.length > 0 ? Math.round(subtaskStatuses.reduce((a, b) => a + b) / subtaskStatuses.length) : 0;

  return (
    <div className="flex items-center justify-between mb-4" onClick={handleTaskClick}>
      <div className="flex items-center space-x-4">
        <div className="flex-shrink-0 w-4 h-4 rounded-full">
          <div className={getProgressBarColor(averageStatus)} style={{ width: `${averageStatus}%`, height: '100%' }}></div>
        </div>
        <p className="font-bold">{task.title}</p>
      </div>
      <button onClick={handleDeleteClick} className="px-2 py-1 text-sm text-red-500 underline">
        Delete
      </button>
    </div>
  );
}

export default Task;

