//SubtaskModal
import React, { useContext, useState } from "react"; // import useState here
import Subtask from "./Subtask";
import TaskInput from "./TaskInput";
import { TaskContext } from "../TaskContext";

function SubtaskModal({ task, onCloseModal, onAddSubtask }) {
	const { addSubtask, updateSubtaskStatus } = useContext(TaskContext);
    const [subtaskTitle, setSubtaskTitle] = useState(""); // Add this line to define subtaskTitle as state

	const handleTitleChange = (event) => {
		setSubtaskTitle(event.target.value);
	};

	const handleAddSubtask = () => {
		if (subtaskTitle) {
			const newSubtask = {
				id: Date.now(),
				title: subtaskTitle,
				status: 0,
			};
			onAddSubtask(task.id, newSubtask);
			setSubtaskTitle("");
		}
	};

	return (
		<div className="fixed top-0 left-0 w-full h-full flex items-center justify-center">
			<div className="absolute top-0 left-0 w-full h-full bg-gray-900 opacity-75"></div>
			<div className="z-10 w-full max-w-md p-6 mx-auto bg-white rounded-md shadow-lg">
				<div className="flex items-center justify-between mb-4">
					<h2 className="font-bold text-lg">{task.title}</h2>
					<button
						onClick={onCloseModal}
						className="px-2 py-1 text-sm font-medium text-gray-500 hover:text-gray-800"
					>
						Close
					</button>
				</div>
				<div className="mb-4">
					{task.subtasks.map((subtask) => (
						<Subtask
							key={subtask.id}
							taskId={task.id}
							subtask={subtask}
						/>
					))}
				</div>
				<div className="flex items-center space-x-4">
					<input
						type="text"
						placeholder="Enter subtask title"
						value={subtaskTitle}
						onChange={handleTitleChange}
						className="w-2/3 px-3 py-2 border border-gray-400 rounded-md"
					/>
					<button
						onClick={handleAddSubtask}
						className="px-4 py-2 bg-blue-500 text-white rounded-md"
					>
						Add
					</button>
				</div>
			</div>
		</div>
	);
}

export default SubtaskModal;



