import React, { useContext } from 'react';
import { TaskContext } from '../TaskContext';
import { FaCircle, FaRegCircle, FaCheckCircle } from 'react-icons/fa';
import { BsQuestionCircle } from 'react-icons/bs';
import ReactTooltip from 'react-tooltip';

function Subtask({ taskId, subtask }) {
  const { updateSubtaskStatus } = useContext(TaskContext);

  const handleStatusChange = (event) => {
    const newStatus = event.target.value;
    updateSubtaskStatus(taskId, subtask.id, newStatus);
  };

  let statusIcon, statusColor, statusText;
  if (subtask.status === 100) {
    statusIcon = <FaCheckCircle size={16} />;
    statusColor = 'bg-green-500';
    statusText = 'Completed';
  } else if (subtask.status > 0) {
    statusIcon = <FaCircle size={16} />;
    statusColor = 'bg-yellow-500';
    statusText = 'Ongoing';
  } else {
    statusIcon = <FaRegCircle size={16} />;
    statusColor = 'bg-gray-400';
    statusText = 'Pending';
  }

  return (
    <div className="flex items-center justify-between mb-2">
      <div className="flex items-center space-x-2">
        <label htmlFor={`subtask-${subtask.id}`} data-tip data-for={`subtask-${subtask.id}-tooltip`}>
          {statusIcon}
        </label>
        <input
          id={`subtask-${subtask.id}`}
          type="radio"
          checked={subtask.status === 100}
          onChange={handleStatusChange}
          value={subtask.status === 100 ? 0 : 100}
          className="sr-only"
        />
        <ReactTooltip id={`subtask-${subtask.id}-tooltip`} place="top" effect="solid">
          {statusText}
        </ReactTooltip>
        <p>{subtask.title}</p>
      </div>
      <div className={`flex-shrink-0 w-4 h-4 rounded-full ${statusColor}`}></div>
    </div>
  );
}

export default Subtask;
