  const symbols = useSelector(selectSpotBinanceSymbolState);


  const handleChangeModal = (asset: string, event: boolean) => {
    setConvertBalances(prevState => prevState?.map(b => b.asset === asset ? ({...b, checked: event}) : b));
  };

  const convertAssetsHandler = () => {
    const assets = convertBalances?.filter(a => a.checked === true).map(a => a.asset);

    if (assets.length === 0) {
